mapper.select:
select

from wlp_user



			AND ID = #{condition.id,jdbcType=VARCHAR}


			USER_NAME = #{condition.userName,jdbcType=VARCHAR}


			MOBILE_PHONE = #{condition.mobilePhone,jdbcType=VARCHAR}


			EMAIL = #{condition.email,jdbcType=VARCHAR}


			LOGIN_PASSWORD = #{condition.loginPassword,jdbcType=VARCHAR}


			TRANS_PASSWORD = #{condition.transPassword,jdbcType=VARCHAR}


			REC_EMAIL = #{condition.recEmail,jdbcType=VARCHAR}


			REMARK like '%${condition.remark}%'


			WECHAT = #{condition.wechat,jdbcType=VARCHAR}


			ALIPAY = #{condition.alipay,jdbcType=VARCHAR}


			BANK_NAME = #{condition.bankName,jdbcType=VARCHAR}


			BANK_USERNAME = #{condition.bankUsername,jdbcType=VARCHAR}


			BANK_ACCT = #{condition.bankAcct,jdbcType=VARCHAR}


			STATUS = #{condition.status,jdbcType=VARCHAR}
